import { AnimationControls, motion } from 'framer-motion'
import { Article } from './Article'
import { Box, Grid, duration } from '@mui/material'
import { Comment } from './Comment'
import { Toggler } from './Toggler'
import { genericHookContextBuilder } from '../helpers/genericHookContextBuilder'
import { theme } from '../helpers/theme'
import { useAnimation } from 'framer-motion'
import { useInView } from 'react-intersection-observer'
import React, { useContext, useEffect, useState } from 'react'
import styled, { keyframes } from 'styled-components'

export type ArticleData = {
  author: string
  date: string
  text: string
}
export type CommentData = {
  id: number
  author: string
  text: string
  date: string
}

const animationOptions = {
  y: 0,
  transition: {
    duration: 1,
    type: 'spring',
    bounce: 0.2,
  },
}

const startAnimation = (condition: boolean, animation: AnimationControls) => {
  if (condition) {
    animation.start(animationOptions)
  } else {
    animation.start({ y: '100vw' })
  }
}

const useLogicState = () => {
  const [articleData, setArticleData] = useState({} as ArticleData)
  const [commentData, setCommentData] = useState([] as CommentData[])
  const [moreCommentData, setMoreCommentData] = useState([] as CommentData[])
  const [darkMode, setDarkMode] = useState(false)
  const [loading, setLoading] = useState(false)
  const [inViewRef, inView] = useInView({ triggerOnce: true })
  const [inViewRef2, inView2] = useInView()
  const animation = useAnimation()
  const animation2 = useAnimation()
  const animation3 = useAnimation()
  const [showMore, setShowMore] = useState(false)

  useEffect(() => {
    setLoading(true)
    setTimeout(() => {
      // @ts-ignore
      if (document.__article) {
        // @ts-ignore
        setArticleData(document.__article)
        // @ts-ignore
        setCommentData(document.__comments)
        // @ts-ignore
        setMoreCommentData(document.__moreComments)
        setLoading(false)
      }
    }, 1000)
  }, [])

  useEffect(() => {
    startAnimation(inView, animation)
  }, [inView])

  useEffect(() => {
    startAnimation(inView2, animation2)
  }, [inView2])

  useEffect(() => {
    startAnimation(showMore, animation3)
  }, [showMore])

  const handleClick = () => {
    setShowMore(true)
  }

  return {
    articleData,
    commentData,
    moreCommentData,
    darkMode,
    loading,
    inViewRef,
    inViewRef2,
    animation,
    animation2,
    animation3,
    showMore,
    handleClick,
    setDarkMode,
  }
}

export const { ContextProvider: AppContextProvider, Context: AppContext } =
  genericHookContextBuilder(useLogicState)

export const AppUseContext = () => {
  return (
    <AppContextProvider>
      <Application />
    </AppContextProvider>
  )
}

export const Application = () => {
  const {
    articleData,
    commentData,
    moreCommentData,
    darkMode,
    loading,
    inViewRef,
    inViewRef2,
    animation,
    animation2,
    animation3,
    showMore,
    handleClick,
    setDarkMode,
  } = useContext(AppContext)

  return (
    <Container darkMode={darkMode}>
      <Grid
        container
        display={'flex'}
        flexDirection={'column'}
        minHeight={'150vh'}
        justifyContent={'space-between'}
      >
        <Grid item>
          <Toggler darkMode={darkMode} handleClick={() => setDarkMode(!darkMode)} />
        </Grid>
        <Grid item flexGrow={1}>
          <Article articleData={articleData} loading={loading} />
        </Grid>
        <div ref={inViewRef}></div>
        <motion.div animate={animation}>
          <Grid item flexGrow={1}>
            {commentData.map(comment => (
              <Comment key={comment.id} commentData={comment} darkMode={darkMode} />
            ))}
          </Grid>
        </motion.div>
        <div ref={inViewRef2}></div>
        {!showMore && (
          <My_Div>
            <motion.div animate={animation2}>
              <Button_MyButton onClick={() => handleClick()}>Load More</Button_MyButton>
            </motion.div>
          </My_Div>
        )}
        <motion.div animate={animation3}>
          {showMore && (
            <Grid item flexGrow={1}>
              {moreCommentData.map(comment => (
                <Comment key={comment.id} commentData={comment} darkMode={darkMode} />
              ))}
            </Grid>
          )}
        </motion.div>
      </Grid>
    </Container>
  )
}

const Container = styled(Box)<{ darkMode: boolean }>`
  background-color: ${props => (props.darkMode ? theme.colors.dark : theme.colors.white)};
  color: ${({ darkMode }) => (!darkMode ? theme.colors.dark : theme.colors.white)};
  transition: all 400ms;
  i,
  a {
    color: ${props => (props.darkMode ? theme.colors.white : theme.colors.dark)};
    transition: color 400ms;
  }
  a:visited {
    color: ${props => (props.darkMode ? theme.colors.white : theme.colors.dark)};
  }
  ul {
    list-style-type: none;
  }
`
export const Button_MyButton = styled.button`
  font-size: 1.5rem;
  border-radius: 20px;
  border: none;
  color: white;
  background-color: ${theme.colors.darkGrey};
  height: 4.5rem;
  width: fit-content;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.5rem;
  &:hover {
    background: ${theme.colors.yellow};
  }
`

const My_Div = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding-bottom: 10px;
`
