import { ArticleData } from './Application'
import { avatar } from '../helpers/Lion.png'
import { theme } from '../helpers/theme'
import React from 'react'
import styled from 'styled-components'

type PropsType = {
  articleData: ArticleData
}

export const Article = (props: PropsType) => {
  let author = props.articleData.author
  let article = props.articleData.text
  let date = props.articleData.date

  return (
    <div>
      <Div_Wrapper>
        <Container>
          {props.articleData && (
            <Content>
              <Header>
                <Img_AuthorImg src={avatar} alt='avatar' />
                <H1>{author}</H1>
              </Header>
              <Body>
                <Post>{article}</Post>
              </Body>
            </Content>
          )}
        </Container>
      </Div_Wrapper>
    </div>
  )
}

const Content = styled.div`
  margin: 0;
  padding: 10px;
  width: 80%;
  ${theme.breakpoint.phone} {
    width: 90%;
  }
`
const Body = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`

const Div_Wrapper = styled.div`
  max-width: 1000px;
  background-color: ${theme.colors.superWhite};
  box-shadow: ${theme.colors.boxShadow4}
  border-radius: 20px;
  margin: 0 auto;
  width: 90%;
  padding: 2rem 0;
  gap: 1rem;
  ${theme.breakpoint.phone} {
    width: 90%;
  }
`
const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`
const H1 = styled.h1`
  max-width: 700px;
  word-break: break-all;
  text-align: start;
  color: ${theme.colors.black};
`
const Header = styled.header`
  display: flex;
  flex-direction: column;
  align-items: start;
  justify-content: start;
  gap: 1rem;
`

const Post = styled.div`
  text-align: left;
  margin-top: 1.5rem;
  font-size: ${theme.fonts.small};
  word-break: break-all;
  color: ${theme.colors.black};
`
const Img_Cover = styled.img`
  width: 100%;
`
const Img_AuthorImg = styled.img`
  max-height: 35px;
  max-width: 35px;
  border-radius: 40px;
`
