import React, { useEffect, useState } from 'react'

export type Article = {
  author: string
  date: string
  text: string
}
export type Comment = {
  id: number
  author: string
  text: string
  date: string
}

export const Application = () => {
  const [articleData, setArticleData] = useState()

  useEffect(() => {
    setTimeout(() => {
      // @ts-ignore
      if (document.__article) {
        // @ts-ignore
        setArticleData(document.__article.author)
      }
    }, 3000)
  }, [])

  return (
    <div>
      <div>{articleData}</div>
    </div>
  )
}
