import { CommentData } from './Application'
import { theme } from '../helpers/theme'
import React from 'react'
import moment from 'moment'
import styled from 'styled-components'

type PropsType = {
  commentData: CommentData
  darkMode: boolean
}

export const Comment = (props: PropsType) => {
  const avatar = require('../helpers/Lion.png')
  const date = moment(props.commentData.date).format('MMMM Do YYYY, h:mm a')
  return (
    <Div_Wrapper             darkMode={props.darkMode}
    >
      <Header>
        <UserInfo>
          <Img_AuthorImg src={avatar} alt='avatar' />
          <UserName>{props.commentData.author}</UserName>
        </UserInfo>
        <Date>{date}</Date>
      </Header>
      <Container>
        <Post>{props.commentData.text}</Post>
      </Container>
    </Div_Wrapper>
  )
}

const UserInfo = styled.div`
  display: flex;
  justify-content: normal;
  align-items: end;
  flex-direction: row;
  padding-left: 10px;
  gap: 0.5rem;
`
const UserName = styled.div`
  font-size: ${theme.fonts.xs};
  color: ${theme.colors.black};
`
const Date = styled.div`
  font-size: ${theme.fonts.xxs};
  color: darkgrey;
`
const Content = styled.div`
  margin: 0;
  padding: 10px;
  width: 80%;
  ${theme.breakpoint.phone} {
    width: 90%;
  }
`
const Body = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`

const Div_Wrapper = styled.div<{ darkMode: boolean }>`
  max-width: 1000px;
  // background-color: ${theme.colors.superWhite};
    background-color: ${props => (props.darkMode ? theme.colors.darkGrey : theme.colors.superWhite)}

  box-shadow: ${theme.colors.boxShadow}
  border-radius: 20px;
  margin: 10px auto;
  width: 90%;
  padding: 1rem 0;
  ${theme.breakpoint.phone} {
    width: 90%;
  }
`
const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`
const H1 = styled.h1`
  max-width: 700px;
  word-break: break-all;
  text-align: start;
  color: ${theme.colors.black};
`
const Header = styled.header`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding-right: 10px;
`

const Post = styled.div`
  text-align: left;
  font-size: ${theme.fonts.xs};
  word-break: break-all;
  color: ${theme.colors.black};
`

const Img_AuthorImg = styled.img`
  max-height: 35px;
  max-width: 35px;
  border-radius: 40px;
`
