import React, { useEffect, useState } from 'react'

export type ArticleData = {
  author: string
  date: string
  text: string
}
export type CommentData = {
  id: number
  author: string
  text: string
  date: string
}

export const Application = () => {
  const [articleData, setArticleData] = useState([] as ArticleData[])
  const [commentData, setCommentData] = useState([] as CommentData[])
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    setLoading(true)
    setTimeout(() => {
      // @ts-ignore
      if (document.__article) {
        // @ts-ignore
        setArticleData(document.__article.author)
        setLoading(false)
      }
    }, 3000)
  }, [])

  return <div>{/*<div>{articleData}</div>*/}</div>
}
